import React, {useState, useEffect} from 'react'
import Button from 'react-bootstrap/Button';
import About from './About/About'
import Stats from './Stats/Stats'

import './PokemonDetail.css'




const PokemonDetail = () => {

    const [pokemon, setPokemon] = useState([]);
    const pageUrl =  window.location.href;
    const id = pageUrl.split("/")[pageUrl.split("/").length - 1];


    useEffect(() => {
        fetchPokemon()
    }, [])


    const fetchPokemon = async () => {

        const promiseArr = [];
        promiseArr.push(
            (await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`)).json()
          );
        const resolvedData = await Promise.all(promiseArr);

        setPokemon(resolvedData);


    }
    console.log(pokemon);

    return (
        <>
        <div className="container">
            <div className="row">
                <div className="col-12 d-flex justify-content-center mb-4">
                <Button href="/">Go back</Button> 
                </div>
                <hr className="mb-4"></hr>
            </div>
        </div>

        <div className="container">
            <div className="row">
                <div className="col-12 ">
                    {pokemon.map(item => (
                        <div className="poke-card" key={item.id}>
                            <div className="col-md-4">
                            <h1 className = "poke-name" >{item.name}</h1>
                            <div className="poke-front-img">
                                <img src={item.sprites.front_default} alt="pokemon" />
                            </div>
                            <div className="poke-front-img">
                                <img src={item.sprites.back_default} alt="pokemon" />
                            </div>
                            </div>
                            <div className="col-md-4">
                                <div className="poke-info">
                                    <About pokemon={pokemon} />    
                                    <Stats pokemon={pokemon} />                            
                                </div>
                            </div>
                        </div>

                    ))}
                 </div>
            </div>
        </div>
        </>
    )
}


export default PokemonDetail
