import {useLocation, useNavigate, Link} from 'react-router-dom'
import axios from 'axios';
import React, {useState, useEffect, useRef} from 'react'

import About from './About/About'
//import Stats from './Stats/Stats'
import Evolution from './Evolution/Evolution'
import Moves from './Moves/Moves'
//import TitleComponent from './TitleComponent'
import './PokemonDetail.css'

const PokemonDetail = () => {

    useEffect(() => {
        fetchPokemon()
    }, [currentPageUrl])


    const [pokemon, setPokemon] = useState([])
    const [currentPageUrl] = useState("https://pokeapi.co/api/v2/pokemon")

    const pageUrl =  window.location.href;
    const pokemonIndex = pageUrl.split("/")[pageUrl.split("/").length - 1];

    const id = pokemonIndex

    const fetchPokemon = async () => {

        const response = await axios.get(currentPageUrl)
        //console.log(response.data.results[id].url);

        const results = await Promise.all(response.data.results.map(res => axios.get(res.url)))
        //console.log(results[id]);

        const promiseArr = [];
        promiseArr.push(
            (await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`)).json()
          );
        const resolvedData = await Promise.all(promiseArr);
        /*return resolvedData.map((item) => {
            return {
              id: item.id,
              name: item.name,
              sprite: item.sprites.front_default,
              ability: item.abilities[0].ability.name,
            };
          });*/
        console.log(resolvedData);
        setPokemon(resolvedData);
        /*setPokemon((currentPokemon) => {
            return [
                currentPokemon,
                resolvedData.map(item => ({

                      id: item.id,
                      name: item.name,
                      sprite: item.sprites.front_default,
                      ability: item.abilities[0].ability.name,
                    }
                  ))
            ]

        }

        )*/
        /*setPokemon((currentPokemon) => {
            return [
                currentPokemon,
                resolvedData.map(res => (
                    {
                        id: res.data.id,
                        name : res.data.name,
                        img: res.data.sprites.other.dream_world.front_default,
                        abilities : res.data.abilities,
                        types : res.data.types,
                        height : res.data.height,
                        weight : res.data.weight,
                        moves : res.data.moves,
                        stats : res.data.stats,
                        species : res.data.species,
                        base_experience : res.data.base_experience
                    }
                ))
            ]

        }

        )*/





    }
    console.log(pokemon);

    return (
        <>
        <Link to="/" state={""}>Back to Home</Link>
        <div className = "poke-single" >

            {pokemon.map(item => (
                <div className="poke-card" key={item.id}>
                <div className="detail-img">
                        <img src={item.img} alt="pokemon" draggable={false} />
                    </div>
                    <div className="poke-name-container">
                        <h1 className = "poke-name" >{item.name}</h1>
                        <About pokemon={pokemon} />

                    </div>
                </div>

            ))}

        </div>
        </>
    )
}


export default PokemonDetail
