import {useLocation, useNavigate} from 'react-router-dom'
import axios from 'axios';
import React, {useState, useEffect, useRef} from 'react'

import About from './About/About'
//import Stats from './Stats/Stats'
import Evolution from './Evolution/Evolution'
import Moves from './Moves/Moves'
//import TitleComponent from './TitleComponent'
import './PokemonDetail.css'

const PokemonDetail = () => {

    const search = useRef()
    const [pokemons, setPokemons] = useState([])
    const [currentPageUrl] = useState("https://pokeapi.co/api/v2/pokemon")

    useEffect(() => {
        fetchPokemons()
    }, [currentPageUrl])



    const fetchPokemons = async () => {

        const response = await axios.get(currentPageUrl)

        const pageUrl =  window.location.href;
        const pokemonIndex = pageUrl.split("/")[pageUrl.split("/").length - 1];

        const id = pokemonIndex -1
        console.log(pageUrl);
        console.log(response.data.results[id].url);
        const results = await Promise.all(response.data.results.map(res => axios.get(res.url)))
        console.log(results[id]);



        setPokemons(currentPokemon => {
            return [
                currentPokemon,
                results.map(res => (
                    {
                        id: res.data.id,
                        name : res.data.name,
                        img: res.data.sprites.other.dream_world.front_default,
                        abilities : res.data.abilities,
                        types : res.data.types,
                        height : res.data.height,
                        weight : res.data.weight,
                        moves : res.data.moves,
                        stats : res.data.stats,
                        species : res.data.species,
                        base_experience : res.data.base_experience
                    }
                ))
            ]

        }
        )
        console.log(currentPokemon)

    }
    return (

        <div className = "poke-single" >

            {pokemons.map(pokemon => (
                <div className="poke-card">
                    <div className="poke-img-container">
                        <img className = "poke-img" src={pokemon.img} alt="pokemon" />
                    </div>
                    <div className="poke-name-container">
                        <h4 className = "poke-name" >{pokemon.name}</h4>
                    </div>
                </div>

            ))}

        </div>

    )
}


export default PokemonDetail
