import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
//import { RiSearch2Line } from "react-icons/ri";
import {useHistory} from 'react-router-dom'
import Card from './Card'



const fetchPokemonData = async (len) => {
  const promiseArr = [];
  for (let i = len; i < len + 50; i++) {
    promiseArr.push(
      (await fetch(`https://pokeapi.co/api/v2/pokemon/${i}`)).json()
    );
  }
  const resolvedData = await Promise.all(promiseArr);
  return resolvedData.map((item) => {
    return {
      id: item.id,
      name: item.name,
      sprite: item.sprites.front_default,
      ability: item.abilities[0].ability.name,
    };
  });
};

const PokemonList = () => {
  const [data, setData] = useState([]);
  const [message, setMessage] = useState("");
  const [isLoading, setLoading] = useState(true);



  const [searchInput, setSearchInput] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setMessage("Loading...");
      const resp = await fetchPokemonData(1);
      setData(resp);
      setLoading(false);
    };
    fetchData();
  }, []);


  window.onscroll = () => {
    if (data.length > 200) {
      setMessage("Reached end of the list!!");
      return;
    }
    if (
      window.innerHeight + document.documentElement.scrollTop ===
      document.documentElement.offsetHeight
    ) {
      setMessage("Loading...");
      setLoading(true);
      fetchPokemonData(data.length).then((newPokemons) => {
        setData([...data, ...newPokemons]);
        setLoading(false);
      });
    }
  };

  return (
    <div className="PokemonList">


      <div className="container">
        <div className="row">
        <div className="d-flex justify-content-center mb-4">
          <div class="form-group has-search">
                  <span class="fa fa-search form-control-feedback">

                  </span>
                  <input type="text" class="form-control"
                  onChange={event => {setSearchInput(event.target.value)}}
                  placeholder="Search" />
              </div>
          </div>
          <hr className="mb-4"></hr>

          { <Card pokemons={pokemons}/> }
        {isLoading && <h1 className="pokemonName">{message}</h1>}
      </div>
    </div>
    </div>

  );
}

export default PokemonList
