import React from 'react'
import {useNavigate} from 'react-router-dom'
//import './Card.css'


const Card = ({pokemons}) => {
    const [searchInput, setSearchInput] = useState('');
    const navigate = useNavigate()
    const handleOnClick = (id) => {
        const pokemon = pokemons.filter(p => p.id === id)
        navigate.push(`/pokemon/${id}`, {
            pokemon : pokemon,
        })
    }

    return (

        pokemons.filter((pokemon) => {
            if (searchInput == "") {
                return pokemon
            } else if (pokemon.name.toLowerCase().includes(searchInput.toLowerCase())){
                return pokemon
            }
        }).map((pokemon, index) => (
        <div className="col-md-3">

        <div className="card poke-card text-center mb-3" key={index} >
              <p className="card-id">{pokemon.id}</p>
              <img className="card-img-top card-img" src={pokemon.sprite} alt={pokemon.name} />
              <h1> {pokemon.name} </h1>
              <div className="card-body">
                  <h5 className="card-title poke-name">{pokemon.name}</h5>
                  <p className="card-id text-center">{pokemon.ability}</p>
                  <p className="card-url text-center">{pokemon.pokemonUrl}</p>

              </div>
        </div>


        </div>

      ))
    )
}

export default Card
