import React from 'react'
import './Stats.css'


const Stats = ({pokemon}) => {

    return (
        <div className="stats">

            {pokemon.map(poke => (
                <div key={poke.id} className="stats-container">
                    <h2>Stats about <span className="text-capitalize">{poke.name}</span></h2>
                    {poke.stats.map((stat, index) => (

                        <div key = {index} className="detail-progress-bar">
                            <div className="stat-name-container">
                                <p>{stat.stat.name} : {stat.base_stat}</p>
                            </div>

                        </div>
                    ))}
                </div>
            ))}
        </div>
    )
}

export default Stats
