import React,{ useState} from 'react'
import {useNavigate} from 'react-router-dom'
//import './Card.css'


const Card = ({pokemons}) => {
    const navigate = useNavigate()
    const handleOnClick = (id) => {
        const pokemon = pokemons.filter(p => p.id === id)
        navigate.push(`/pokemon/${id}`, {
            pokemon : pokemon,
        })
    }

    return (

          <div className="col-md-3">

          <div className="card poke-card text-center mb-3" key={pokemons.id} >
                <p className="card-id">{pokemons.id}</p>
                <img className="card-img-top card-img" src={pokemons.sprite} alt={pokemons.name} />
                <h1> {pokemons.name} </h1>
                <div className="card-body">
                    <h5 className="card-title poke-name">{pokemons.name}</h5>
                    <p className="card-id text-center">{pokemons.ability}</p>

                </div>
          </div>


          </div>
    )
}

export default Card
